import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import './style.css';
import { Button, Grid } from '@material-ui/core';
import ReplayIcon from '@material-ui/icons/Replay';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slide from '@material-ui/core/Slide';
import endpoints from '../../../hoc/config/endpoints';
import Slider from "react-slick";
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import { useParams } from 'react-router-dom';



const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

function SampleNextArrow(props) {
    const { className, style, onClick } = props;

    return (
        <div
            onClick={onClick}
        >
            <ArrowForwardIosIcon className={className} />
        </div>
    );
}

function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            onClick={onClick}
        >
            <ArrowBackIosNewIcon className={className} />
        </div>
    );
}




export default function Details() {
    const [dataList, setDataList] = useState([]);
    const [categoryList, setCategoryList] = useState([]);
    const [quality360flag, setQuality360flag] = useState(false);
    const [quality480flag, setQuality480flag] = useState(false);
    const [quality720flag, setQuality720flag] = useState(false);
    const [quality1080flag, setQuality1080flag] = useState(false);
    const [quality, setQuality] = useState('');
    const { id } = useParams();
    const vidRef = useRef(null);
    const [isPlaying, setIsPlaying] = useState(false);

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        autoplay: false,
        autoplaySpeed: 5000,
        arrows: true,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };

    function handleAllData(id) {
        let videoById = atob(id);
        axios.get(`${endpoints.getVideoByid}?id=${videoById}`).then((response) => {
            if (response.status === 200) {
                setDataList(response.data);
                hanldeGetCategoryList(response?.data[0]?.Genre);
            }
        })
    }

    function hanldeGetCategoryList(Genre) {
        axios.get(`${endpoints.getByCategory}?category=${Genre}`).then((response) => {
            if (response.status === 200) {
                setCategoryList(response.data);
            }
        })
    }

    useEffect(() => {
        handleAllData(id);
    }, [])

    function handleQuality() {
        setQuality480flag(true);
        setQuality720flag(false);
        setQuality360flag(false);
        setQuality1080flag(false);
    }
    function handle480Quality() {
        setQuality720flag(true);
        setQuality360flag(false);
        setQuality480flag(false);
        setQuality1080flag(false);
    }

    function handle720Quality() {
        setQuality1080flag(true);
        setQuality360flag(false);
        setQuality480flag(false);
        setQuality720flag(false);

    }
    function handle1080Quality() {
        setQuality360flag(true);
        setQuality480flag(false);
        setQuality720flag(false);
        setQuality1080flag(false);
    }

    function playVideo() {
        vidRef.current.play();
        setIsPlaying(!isPlaying);
    }

    function pauseVideo() {
        vidRef.current.pause();
        setIsPlaying(!isPlaying);
    }

    function farwardVideo() {
        vidRef.current.currentTime = vidRef.current.currentTime + 10;
    }
    function backwardVideo() {
        vidRef.current.currentTime = vidRef.current.currentTime - 10;

    }

    function handleStartOver() {
        vidRef.current.currentTime = 0;
    }


    return (
        <div>
            {dataList.map((item, index) =>
                <>
                    <Grid className="main grid" container spacing={3} key={index}>
                        <Grid className="main" item md={12} xs={12}>
                            <Grid className="image-grid" item md={12} xs={12}>
                                {/* <img src={item?.MoviePoster} alt="videoByIdImage" /> */}
                                {quality1080flag ? (
                                    <>
                                        <video
                                            ref={vidRef}
                                            poster={item?.MoviePoster}
                                            controls
                                            width="100%"
                                            controlsList="nodownload"
                                        >
                                            <source
                                                src={item?.Quality1080}
                                                type="video/mp4"
                                            ></source>
                                        </video>
                                        <Button
                                            onClick={() => handle1080Quality()}
                                            style={{ background: 'khaki', color: 'black', right: '65px', bottom: '125px', float: 'right' }}
                                        >
                                            {'HD'}
                                        </Button>
                                        {isPlaying ? (
                                            <Button

                                                onClick={() => pauseVideo()}>
                                                <img src="/images/player/Pause.png" alt="pause" className="playButtons" />
                                            </Button>
                                        ) : (

                                            <Button

                                                onClick={() => playVideo()}>
                                                <img src="/images/player/play.png" alt="play" className="playButtons" />
                                            </Button>
                                        )}
                                        <div>
                                            <Button
                                                onClick={() => backwardVideo()} className="backwardImage">
                                                <img
                                                    src="/images/player/backward.png"
                                                    alt="backward"
                                                />
                                            </Button>

                                            <Button

                                                onClick={() => farwardVideo()} className="farwordImage">
                                                <img
                                                    src="/images/player/forward.png"
                                                    alt="farward"
                                                />
                                            </Button>
                                        </div>
                                    </>
                                ) : (
                                    quality720flag ? (
                                        <>
                                        <div className='xdContainer'></div>
                                            <video
                                                ref={vidRef}
                                                poster={item?.MoviePoster}
                                                controls
                                                width="100%"
                                                controlsList="nodownload"
                                            >
                                                <source
                                                    src={item?.Quality720}
                                                    type="video/mp4"
                                                ></source>
                                            </video>
                                            <Button
                                                onClick={() => handle720Quality()}
                                                style={{ background: 'khaki', color: 'black', right: '65px', bottom: '125px', float: 'right' }}
                                            >
                                                {'SD'}
                                            </Button>
                                            {isPlaying ? (
                                                <Button

                                                    onClick={() => pauseVideo()}>
                                                    <img src="/images/player/Pause.png" alt="pause" className="playButtons" />
                                                </Button>
                                            ) : (

                                                <Button

                                                    onClick={() => playVideo()}>
                                                    <img src="/images/player/play.png" alt="play" className="playButtons" />
                                                </Button>
                                            )}
                                            <div>
                                                <Button
                                                    onClick={() => backwardVideo()} className="backwardImage">
                                                    <img
                                                        src="/images/player/backward.png"
                                                        alt="backward"
                                                    />
                                                </Button>

                                                <Button

                                                    onClick={() => farwardVideo()} className="farwordImage" >
                                                    <img
                                                        src="/images/player/forward.png"
                                                        alt="farward"
                                                    />
                                                </Button>
                                            </div>
                                        </>
                                    ) : (
                                        quality480flag ?
                                            (
                                                <>
                                                    <video
                                                        ref={vidRef}
                                                        poster={item?.MoviePoster}
                                                        controls
                                                        width="100%"
                                                        controlsList="nodownload"
                                                    ></video>
                                                    <source
                                                        src={item?.Quality480}
                                                        type="video/mp4"
                                                    ></source>
                                                    <Button
                                                        onClick={() => handle480Quality()}
                                                        style={{ background: 'khaki', color: 'black', right: '65px', bottom: '125px', float: 'right' }}
                                                    >
                                                        {'SUB'}
                                                    </Button>
                                                    {isPlaying ? (
                                                        <Button

                                                            onClick={() => pauseVideo()}>
                                                            <img src="/images/player/Pause.png" alt="pause" className="playButtons" />
                                                        </Button>
                                                    ) : (

                                                        <Button

                                                            onClick={() => playVideo()}>
                                                            <img src="/images/player/play.png" alt="play" className="playButtons" />
                                                        </Button>
                                                    )}
                                                    <div>
                                                        <Button
                                                            onClick={() => backwardVideo()} className="backwardImage">
                                                            <img
                                                                src="/images/player/backward.png"
                                                                alt="backward"
                                                            />
                                                        </Button>

                                                        <Button

                                                            onClick={() => farwardVideo()} className="farwordImage">
                                                            <img
                                                                src="/images/player/forward.png"
                                                                alt="farward"
                                                            />
                                                        </Button>
                                                    </div>
                                                </>
                                            )
                                            :
                                            (
                                                <>
                                                    <video
                                                        ref={vidRef}
                                                        poster={item?.MoviePoster}
                                                        controls
                                                        width="100%"
                                                        controlsList="nodownload"
                                                    >
                                                        <source
                                                            src={item?.Quality360}
                                                            type="video/mp4"
                                                        ></source>
                                                    </video>
                                                    <Button
                                                        onClick={() => handleQuality()}
                                                        style={{ background: 'khaki', color: 'black', right: '65px', bottom: '125px', float: 'right' }}
                                                    >
                                                        {'LOW'}
                                                    </Button>
                                                    <div>
                                                        {isPlaying ? (
                                                            <Button

                                                                onClick={() => pauseVideo()}>
                                                                <img
                                                                    src="/images/player/Pause.png"
                                                                    alt="pause" className="playButtons"
                                                                />
                                                            </Button>
                                                        ) : (

                                                            <Button

                                                                onClick={() => playVideo()}>
                                                                <img
                                                                    src="/images/player/play.png"
                                                                    alt="play" className="playButtons"
                                                                />
                                                            </Button>
                                                        )}
                                                    </div>
                                                    <div>
                                                        <Button
                                                            onClick={() => backwardVideo()} className="backwardImage">
                                                            <img
                                                                src="/images/player/backward.png"
                                                                alt="backward"
                                                            />
                                                        </Button>

                                                        <Button

                                                            onClick={() => farwardVideo()} className="farwordImage">
                                                            <img
                                                                src="/images/player/forward.png"
                                                                alt="farward"
                                                            />
                                                        </Button>
                                                    </div>
                                                </>
                                            )
                                    )
                                )
                                }
                            </Grid>
                        </Grid>
                    </Grid>

                    <Grid className="main-grid" container spacing={3} >
                        <Grid className="tile-main" item md={12} xs={12}>
                            <Grid className="icon" item md={3} xs={3} onClick={() => handleStartOver()} style={{ cursor: 'pointer' }}>
                                <ReplayIcon className="icon-replay" />
                                <h5 className="start-over" >Start Over</h5>
                            </Grid>

                            <Grid className="icon" item md={6} xs={6}>
                                <h3>{item?.MovieName}</h3>
                                <p><span>{item?.Genre} |</span><span>{item?.IMDbRating}|</span> <span>{item?.Certificate}|</span><span>{item?.Duration}|</span><span>{item?.ReleasedYear.split(' ')[0].split('/')[2]}</span></p>
                            </Grid>
                            <Grid className="last-grid" item md={3} xs={3}>
                                <img src="/images/My collection.png" className="collection" />
                                <h5>Add to collection</h5>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid className="description padd" container>
                        <Grid className="description-main" item md={12} xs={12}>
                            <Grid className="description-text" item md={12} xs={12}>
                                <h5>description</h5>
                                <p className="description-para">
                                    {item?.Description}
                                </p>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid className="crew padd" container >
                        <Grid className="crew-main" item md={12} xs={12}>
                            <Grid className="crew-text" item md={12} xs={12}>
                                <h5>cast & crew</h5>
                            </Grid>
                        </Grid>
                        <Grid className="crew-images" item md={12} xs={12}>
                            {item?.cast?.map((cas, index) =>
                                <Grid className="crew-images1" item md={1} xs={4}>
                                    <img src={cas?.ImageURL} alt="castAndCrew" />
                                    <p>{cas?.Role}</p>
                                    <p>{cas?.Charactor}</p>
                                </Grid>
                            )}
                        </Grid>
                    </Grid>

                </>
            )}
            <Grid className="realted padd" container>
                <Grid className="related-main" item md={12} xs={12}>
                    <div className="iq-main-header d-flex align-items-center justify-content-between">
                        <h4 className='main-title'>Related Movies</h4>
                        <a className="iq-view-all" href="#">View All</a>
                    </div>
                    <Slider {...settings}>
                        {categoryList.map((category, index) => (
                            <div className='highlyRatedslider'
                            //  onClick={() => handleHighlyRated(language)}
                            >
                                <img className='highlyRatedMovies' src={category?.MoviePoster} />
                            </div>
                        )
                        )
                        }
                    </Slider>
                </Grid>
            </Grid>
        </div>
    )
}
