import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './style.css';
import { Grid } from '@material-ui/core';
import ReplayIcon from '@material-ui/icons/Replay';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slide from '@material-ui/core/Slide';
import endpoints from '../../../hoc/config/endpoints';
import Slider from "react-slick";
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            onClick={onClick}
        >
            <ArrowForwardIosIcon className={className} />
        </div>
    );
}

function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            onClick={onClick}
        >
            <ArrowBackIosNewIcon className={className} />
        </div>
    );
}




export default function Details(){ 
    const [dataList, setDataList] = useState([]);

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        autoplay: false,
        autoplaySpeed: 5000,
        arrows: true,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };

    function handleAllData() {
        axios.get(`${endpoints.getAllVideos}`).then((response) => {
            console.log('response', response);
            if (response.status === 200) {
                setDataList(response.data);
            }
        })
    }

    useEffect(() => {
        handleAllData();
    }, [])


    return(
        <div>
         <Grid className="main grid" container spacing={3}></Grid>
             <Grid className="main grid" container spacing={3}>
             <Grid className="main" md={12} xs={12}>
                    <Grid className="image-grid" md={12} xs={12}>
                        <img src="http://iott.co.in/Images/allarodu.jpg"/>
                    </Grid>              
             </Grid>
            </Grid>

            <Grid className="main-grid" container spacing={3}>
            <Grid className="tile-main"  md={12} xs={12}>
            <Grid className="icon" md={3} xs={3}>
                     <ReplayIcon className="icon-replay"/>
                     <h5 className="start-over">Start Over</h5>
                    </Grid>

                    <Grid className="icon" md={6} xs={6}>
                      <h3>kgf</h3>
                      <p><span>Drama |</span><span>IMDb 12|</span> <span>U/A|</span><span>2h 30min|</span><span>2019</span></p>
                    </Grid>
                    <Grid className="last-grid" md={3} xs={3}>
                        <img src="images/My collection.png" className="collection"/>
                        <h5>Add to collection</h5>
                    </Grid>
            </Grid>
         </Grid>
         <Grid className="description" container>
             <Grid className="description-main" md={12} xs={12}>
                 <Grid className="description-text" md={12} xs={12}> 
                     <h5>description</h5>
                     <p className="description-para">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                 </Grid>
             </Grid>
         </Grid>
         <Grid className="crew" container>
             <Grid className="crew-main" md={12} xs={12}>
                 <Grid className="crew-text" md={12} xs={12}> 
                    <h5>cast & crew</h5>
                 </Grid>
             </Grid>
             <Grid className="crew-images" md={12} xs={12}>
                 <Grid className="crew-images1" md={1} xs={4}>
                     <p>hero</p>
                     <img src="./images/favorite/kgf.jpeg"></img>
                     <p>yash</p>
                 </Grid>
                 <Grid className="crew-images2" md={1} xs={4}>
                     <p>hero</p>
                     <img src="./images/favorite/kgf.jpeg"></img>
                     <p>yash</p>
                 </Grid>
                 <Grid className="crew-images3" md={1} xs={4}>
                     <p>hero</p>
                     <img src="./images/favorite/kgf.jpeg"></img>
                     <p>yash</p>
                 </Grid>
                 <Grid className="crew-images4" md={1} xs={4}>
                     <p>hero</p>
                     <img src="./images/favorite/kgf.jpeg"></img>
                     <p>yash</p>
                 </Grid>
                 <Grid className="crew-images4" md={1} xs={4}>
                     <p>hero</p>
                     <img src="./images/favorite/kgf.jpeg"></img>
                     <p>yash</p>
                 </Grid>
             </Grid>
         </Grid>
         <Grid className="realted" container>
             <Grid className="related-main" md={12} xs={12}>
         <div className="iq-main-header d-flex align-items-center justify-content-between">
                <h4 className='main-title'>Related Movies</h4>
                <a className="iq-view-all" href="movie-category.html">View All</a>
            </div>
            <Slider {...settings}>
                {dataList.map((data, index) => (
                    <div className='highlyRatedslider'
