import React, { useEffect, useState } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
import axios from 'axios';
import endpoints from '../../../hoc/config/endpoints';
import { useNavigate } from "react-router";

export default function HomeSlider() {
    const [videosList, setVideosList] = useState([]);
    const navigate = useNavigate();

    function handleGetAllVideos() {
        axios.get(`${endpoints.getAllImages}`).then((response) => {
            if (response.status === 200) {
                setVideosList(response.data);
            }
        })
    }
    function handleDetailsPage() {
        navigate('/details')
    }

    useEffect(() => {
        handleGetAllVideos();
    }, [])

    return (
        <div>
            <Carousel

                autoPlay
                interval="3000"
                transitionTime="350"
                showThumbs={false}
                infiniteLoop
                // axis='vertical'
                // verticalSwipe
                // showIndicators={false}
                // style={{ height: 'auto' }}

            >
                {videosList.map((video, index) =>
                    <div onClick={handleDetailsPage} key={index + 1} >
                       {} console.log(video.Id);
                        <img src={video?.URL} />
                        {/* <p className="legend">{video?.MovieName}</p> */}
                    </div>
                )}
            </Carousel>
        </div>
    );
}
